            Console.Write("    ");
            Console.SetCursorPosition(coordinates[0] - 1, coordinates[1] - 1);
            Console.Write("    ");
            Console.SetCursorPosition(coordinates[0] - 1, coordinates[1]);
            Console.Write("    ");
            Console.SetCursorPosition(coordinates[0] - 1, coordinates[1] + 1);
            Console.Write("    ");
            Console.SetCursorPosition(coordinates[0], coordinates[1] + 2);
            Console.Write("    ");
        
        }
        
